import { useState, useEffect } from 'react';
//import ItemCount from '../ItemCount/ItemCount'
import Spinner from '../Spinner/Spinner';
import ItemDetail from '../ItemDetail/ItemDetail';
import { useParams } from 'react-router-dom'

const productos = [
    { title: 'Intel i3', description: "Procesador i3, listo para ofimatica", img: "data:image/jpeg;base64,", category: "procesadores", price: 100, id: 1 },
    { title: 'Intel i5', description: "Procesador i5, ideal para uso hogare√±o", img: "data:image/jpeg;base64,", category: "procesadores", price: 100, id: 2 },
    { title: 'Intel i7', description: "Procesador i7, perfecto para entusiastas", img: "data:image/jpeg;base64,", category: "procesadores", price: 100, id: 3 },
    { title: 'GPU', description: "Perfecta grafica para tus mejores juegos", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxK_XTraO1CFhnAdyY5xXfel-r-ywyrKcXpw&usqp=CAU", category: "graficas", price: 14, id: 4 },
    { title: 'RAM', description: "Memoria ram de 8gb marca HyperX", img: "https://http2.mlstatic.com/D_NQ_NP_635049-MLA44337467037_122020-O.webp", category: "rams", price: 14, id: 5 },
    { title: 'SSD', description: "Disco de estado solido marca Kingstone", img: "https://http2.mlstatic.com/D_NQ_NP_617579-MLA40103563286_122019-O.webp", category: "ssds", price: 14, id: 6 }

];

const getFetch = new Promise((resolve, reject) => {
    setTimeout(() => {
        resolve(productos)
    }, 2000)
})

const ItemDetailContainer = (props) => {

    const [products, setProducts] = useState([])
    const [loading, setLoading] = useState(true)

    const { itemId } = useParams()

    useEffect(() => {
        getFetch
            .then(res => {
                setProducts(res)
            })
            .catch(err => console.log(err))
            .finally(() => setLoading(false))
    }, [itemId])

    return (

        <div className="contenido" >
            {
                loading ?
                    <Spinner />
                    :
                    <ItemDetail {...products[itemId - 1]} />
            }

        </div >
    )
}

export default ItemDetailContainer
